import React, { useState } from 'react';
import { mockFacilities, mockESGData } from '../../utils/mockData';
import { Building, TrendingUp, Users, MapPin, Calendar, Factory, Zap, Shield, Leaf } from 'lucide-react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell, LineChart, Line } from 'recharts';

export const AdminDashboard: React.FC = () => {
  const [selectedFacility, setSelectedFacility] = useState<string | null>(null);

  const totalFacilities = mockFacilities.length;
  const avgESGScore = Math.round(
    mockFacilities.reduce((acc, facility) => acc + facility.esgScore, 0) / totalFacilities
  );

  // Calculate total production capacity
  const totalCapacity = mockFacilities.reduce((sum, facility) => {
    return sum + parseFloat(facility.capacity.replace(' MTPA', ''));
  }, 0);

  // Calculate weighted average CO2 per ton (weighted by production capacity)
  const weightedAvgCO2 = Math.round(
    mockFacilities.reduce((acc, facility) => {
      const capacity = parseFloat(facility.capacity.replace(' MTPA', ''));
      const co2 = mockESGData[facility.id].environmental.co2PerTon;
      return acc + (co2 * capacity);
    }, 0) / totalCapacity
  );

  // Calculate average alternative fuel usage
  const avgAltFuelUsage = Math.round(
    mockFacilities.reduce((acc, facility) => {
      return acc + mockESGData[facility.id].environmental.altFuelUsage;
    }, 0) / totalFacilities
  );

  // Calculate group LTIFR (Lost Time Injury Frequency Rate)
  const totalEmployees = mockFacilities.reduce((acc, facility) => {
    return acc + mockESGData[facility.id].social.employees;
  }, 0);
  const weightedLTIFR = (
    mockFacilities.reduce((acc, facility) => {
      const employees = mockESGData[facility.id].social.employees;
      const ltifr = mockESGData[facility.id].social.ltifr;
      return acc + (ltifr * employees);
    }, 0) / totalEmployees
  ).toFixed(2);

  const chartData = mockFacilities.map(facility => ({
    name: facility.name,
    capacity: parseFloat(facility.capacity.replace(' MTPA', '')),
    co2PerTon: mockESGData[facility.id].environmental.co2PerTon,
    altFuel: mockESGData[facility.id].environmental.altFuelUsage
  }));

  const plantTypeData = [
    { name: 'Integrated Plants', value: mockFacilities.filter(f => f.type === 'integrated').length, color: '#074D47' },
    { name: 'Grinding Units', value: mockFacilities.filter(f => f.type === 'grinding').length, color: '#22867C' }
  ];

  const co2TrendData = [
    { month: 'Jan', avgCO2: 720, target: 650 },
    { month: 'Feb', avgCO2: 710, target: 650 },
    { month: 'Mar', avgCO2: 700, target: 650 },
    { month: 'Apr', avgCO2: 695, target: 650 },
    { month: 'May', avgCO2: 685, target: 650 },
    { month: 'Jun', avgCO2: weightedAvgCO2, target: 650 }
  ];

  const selectedFacilityData = selectedFacility ? mockFacilities.find(f => f.id === selectedFacility) : null;
  const selectedESGData = selectedFacility ? mockESGData[selectedFacility] : null;

  return (
    <div className="space-y-grid-6">
      <div className="flex items-center justify-between border-b border-gray-200 pb-grid-3">
        <h1 className="text-2xl font-semibold text-latspace-dark">Admin Dashboard</h1>
        <div className="text-sm text-latspace-medium font-mono">
          {new Date().toLocaleDateString()}
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-grid-3">
        <div className="bg-white border border-gray-200 p-grid-3 grid-pattern">
          <div className="flex items-center">
            <Building className="w-6 h-6 text-latspace-dark" />
            <div className="ml-grid-2">
              <p className="text-xs font-semibold text-latspace-medium uppercase tracking-wider">Total Facilities</p>
              <p className="text-2xl font-mono text-latspace-dark data-value">{totalFacilities}</p>
            </div>
          </div>
        </div>

        <div className="bg-white border border-gray-200 p-grid-3 grid-pattern">
          <div className="flex items-center">
            <TrendingUp className="w-6 h-6 text-latspace-dark" />
            <div className="ml-grid-2">
              <p className="text-xs font-semibold text-latspace-medium uppercase tracking-wider">Avg ESG Score</p>
              <p className="text-2xl font-mono text-latspace-dark data-value">{avgESGScore}</p>
            </div>
          </div>
        </div>

        <div className="bg-white border border-gray-200 p-grid-3 grid-pattern">
          <div className="flex items-center">
            <Users className="w-6 h-6 text-latspace-dark" />
            <div className="ml-grid-2">
              <p className="text-xs font-semibold text-latspace-medium uppercase tracking-wider">Avg Completion</p>
              <p className="text-2xl font-mono text-latspace-dark data-value">{avgDataCompletion}%</p>
            </div>
          </div>
        </div>

        <div className="bg-white border border-gray-200 p-grid-3 grid-pattern">
          <div className="flex items-center">
            <Calendar className="w-6 h-6 text-latspace-dark" />
            <div className="ml-grid-2">
              <p className="text-xs font-semibold text-latspace-medium uppercase tracking-wider">Active Today</p>
              <p className="text-2xl font-mono text-latspace-dark data-value">
                {mockFacilities.filter(f => f.lastUpdated === new Date().toISOString().split('T')[0]).length}
              </p>
            </div>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-grid-3">
        <div className="bg-white border border-gray-200 p-grid-4">
          <h3 className="text-base font-semibold text-latspace-dark mb-grid-3 uppercase tracking-wide">ESG Performance Overview</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={chartData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="esgScore" fill="#074D47" name="ESG Score" />
              <Bar dataKey="completion" fill="#22867C" name="Completion %" />
            </BarChart>
          </ResponsiveContainer>
        </div>

        <div className="bg-white border border-gray-200 p-grid-4">
          <h3 className="text-base font-semibold text-latspace-dark mb-grid-3 uppercase tracking-wide">Performance Distribution</h3>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={pieData}
                cx="50%"
                cy="50%"
                outerRadius={80}
                dataKey="value"
                label={({ name, value }) => `${name}: ${value}`}
              >
                {pieData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={entry.color} />
                ))}
              </Pie>
              <Tooltip />
            </PieChart>
          </ResponsiveContainer>
        </div>
      </div>

      <div className="bg-white border border-gray-200">
        <div className="px-grid-4 py-grid-3 border-b border-gray-200">
          <h3 className="text-base font-semibold text-latspace-dark uppercase tracking-wide">Facility Overview</h3>
        </div>
        <div className="p-grid-4">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-grid-2 mb-grid-4">
            {mockFacilities.map((facility) => (
              <div
                key={facility.id}
                className={`p-grid-3 border cursor-pointer transition-colors ${
                  selectedFacility === facility.id
                    ? 'border-latspace-dark bg-gray-50'
                    : 'border-gray-200 hover:border-latspace-medium'
                }`}
                onClick={() => setSelectedFacility(
                  selectedFacility === facility.id ? null : facility.id
                )}
              >
                <div className="flex items-center justify-between mb-grid">
                  <h4 className="font-semibold text-latspace-dark">{facility.name}</h4>
                  <div className="px-grid py-1 text-xs font-mono border border-latspace-dark text-latspace-dark data-value">
                    {facility.esgScore}
                  </div>
                </div>
                <div className="flex items-center text-xs text-latspace-medium mb-grid font-mono">
                  <MapPin className="w-3 h-3 mr-1" />
                  {facility.location}
                </div>
                <div className="flex justify-between text-xs">
                  <span className="text-latspace-medium">{facility.manager}</span>
                  <span className="text-latspace-dark font-mono data-value">{facility.dataCompleteness}%</span>
                </div>
              </div>
            ))}
          </div>

          {selectedFacilityData && selectedESGData && (
            <div className="border-t border-gray-200 pt-grid-4">
              <h4 className="text-base font-semibold text-latspace-dark mb-grid-3 uppercase tracking-wide">
                {selectedFacilityData.name} - Detailed Metrics
              </h4>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-grid-3">
                <div className="border border-gray-200 p-grid-3 grid-pattern">
                  <h5 className="font-semibold text-latspace-dark mb-grid-2 text-sm uppercase tracking-wider">Environmental</h5>
                  <div className="space-y-grid text-xs">
                    <div className="flex justify-between">
                      <span className="text-latspace-medium">Emissions (tons CO2)</span>
                      <span className="font-mono text-latspace-dark data-value">{selectedESGData.environmental.emissions}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-latspace-medium">Energy (MWh)</span>
                      <span className="font-mono text-latspace-dark data-value">{selectedESGData.environmental.energy}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-latspace-medium">Water (gallons)</span>
                      <span className="font-mono text-latspace-dark data-value">{selectedESGData.environmental.water}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-latspace-medium">Waste (tons)</span>
                      <span className="font-mono text-latspace-dark data-value">{selectedESGData.environmental.waste}</span>
                    </div>
                  </div>
                </div>

                <div className="border border-gray-200 p-grid-3 grid-pattern">
                  <h5 className="font-semibold text-latspace-dark mb-grid-2 text-sm uppercase tracking-wider">Social</h5>
                  <div className="space-y-grid text-xs">
                    <div className="flex justify-between">
                      <span className="text-latspace-medium">Employees</span>
                      <span className="font-mono text-latspace-dark data-value">{selectedESGData.social.employees}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-latspace-medium">Safety Score</span>
                      <span className="font-mono text-latspace-dark data-value">{selectedESGData.social.safety}%</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-latspace-medium">Community Score</span>
                      <span className="font-mono text-latspace-dark data-value">{selectedESGData.social.community}%</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-latspace-medium">Diversity Score</span>
                      <span className="font-mono text-latspace-dark data-value">{selectedESGData.social.diversity}%</span>
                    </div>
                  </div>
                </div>

                <div className="border border-gray-200 p-grid-3 grid-pattern">
                  <h5 className="font-semibold text-latspace-dark mb-grid-2 text-sm uppercase tracking-wider">Governance</h5>
                  <div className="space-y-grid text-xs">
                    <div className="flex justify-between">
                      <span className="text-latspace-medium">Compliance</span>
                      <span className="font-mono text-latspace-dark data-value">{selectedESGData.governance.compliance}%</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-latspace-medium">Ethics Score</span>
                      <span className="font-mono text-latspace-dark data-value">{selectedESGData.governance.ethics}%</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-latspace-medium">Transparency</span>
                      <span className="font-mono text-latspace-dark data-value">{selectedESGData.governance.transparency}%</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-latspace-medium">Board Diversity</span>
                      <span className="font-mono text-latspace-dark data-value">{selectedESGData.governance.boardDiversity}%</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};
