import React from 'react';
import { CheckCircle, Clock, AlertCircle } from 'lucide-react';

interface ProgressBarProps {
  percentage: number;
  label?: string;
  showDetails?: boolean;
}

export const ProgressBar: React.FC<ProgressBarProps> = ({ 
  percentage, 
  label = "Data Completion", 
  showDetails = true 
}) => {
  const getStatusColor = (percent: number) => {
    if (percent >= 90) return 'bg-green-500';
    if (percent >= 70) return 'bg-yellow-500';
    return 'bg-red-500';
  };

  const getStatusIcon = (percent: number) => {
    if (percent >= 90) return <CheckCircle className="w-5 h-5 text-green-500" />;
    if (percent >= 70) return <Clock className="w-5 h-5 text-yellow-500" />;
    return <AlertCircle className="w-5 h-5 text-red-500" />;
  };

  const getStatusText = (percent: number) => {
    if (percent >= 90) return 'Excellent';
    if (percent >= 70) return 'Good Progress';
    return 'Needs Attention';
  };

  return (
    <div className="bg-white rounded-lg p-6 shadow-sm">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold text-gray-900">{label}</h3>
        {showDetails && getStatusIcon(percentage)}
      </div>

      <div className="mb-4">
        <div className="flex justify-between text-sm text-gray-600 mb-2">
          <span>Progress</span>
          <span>{percentage.toFixed(1)}%</span>
        </div>
        <div className="w-full bg-gray-200 rounded-full h-3">
          <div
            className={`h-3 rounded-full transition-all duration-500 ${getStatusColor(percentage)}`}
            style={{ width: `${percentage}%` }}
          />
        </div>
      </div>

      {showDetails && (
        <div className="flex items-center justify-between text-sm">
          <span className="text-gray-600">Status</span>
          <span className={`font-medium ${
            percentage >= 90 ? 'text-green-600' : 
            percentage >= 70 ? 'text-yellow-600' : 
            'text-red-600'
          }`}>
            {getStatusText(percentage)}
          </span>
        </div>
      )}
    </div>
  );
};
