  const facility = mockFacilities.find(f => f.id === user?.facilityId);
  const esgData = user?.facilityId ? mockESGData[user.facilityId] : null;

  if (!facility || !esgData) {
    return (
      <div className="text-center py-12">
        <p className="text-gray-500">Facility data not found</p>
      </div>
    );
  }

  const environmentalMetrics = [
    { icon: Leaf, label: 'CO2 Emissions', value: esgData.environmental.emissions, unit: 'tons', color: 'text-green-600' },
    { icon: Zap, label: 'Energy Usage', value: esgData.environmental.energy, unit: 'MWh', color: 'text-yellow-600' },
    { icon: Droplets, label: 'Water Usage', value: esgData.environmental.water, unit: 'gallons', color: 'text-blue-600' },
    { icon: Trash2, label: 'Waste Generated', value: esgData.environmental.waste, unit: 'tons', color: 'text-red-600' }
  ];

  const socialMetrics = [
    { icon: Users, label: 'Total Employees', value: esgData.social.employees, unit: '', color: 'text-purple-600' },
    { icon: Shield, label: 'Safety Score', value: esgData.social.safety, unit: '%', color: 'text-green-600' },
    { icon: Heart, label: 'Community Impact', value: esgData.social.community, unit: '%', color: 'text-pink-600' },
    { icon: Scale, label: 'Diversity Index', value: esgData.social.diversity, unit: '%', color: 'text-indigo-600' }
  ];

  const governanceMetrics = [
    { label: 'Compliance', value: esgData.governance.compliance, unit: '%' },
    { label: 'Ethics', value: esgData.governance.ethics, unit: '%' },
    { label: 'Transparency', value: esgData.governance.transparency, unit: '%' },
    { label: 'Board Diversity', value: esgData.governance.boardDiversity, unit: '%' }
  ];

  const monthlyTrend = [
    { month: 'Jan', esg: 75, completion: 70 },
    { month: 'Feb', esg: 76, completion: 72 },
    { month: 'Mar', esg: 77, completion: 75 },
    { month: 'Apr', esg: 78, completion: 80 },
    { month: 'May', esg: 79, completion: 82 },
    { month: 'Jun', esg: facility.esgScore, completion: facility.dataCompleteness }
  ];

