    { subject: 'Environmental', A: 85, fullMark: 100 },
    { subject: 'Social', A: 88, fullMark: 100 },
    { subject: 'Governance', A: 92, fullMark: 100 },
    { subject: 'Innovation', A: 78, fullMark: 100 },
    { subject: 'Risk Management', A: 82, fullMark: 100 }
  ];

  const recentActivities = [
    { id: 1, type: 'upload', description: 'Uploaded energy consumption report', time: '2 hours ago', icon: Upload },
    { id: 2, type: 'entry', description: 'Updated safety metrics', time: '1 day ago', icon: FileText },
    { id: 3, type: 'complete', description: 'Completed Q2 ESG assessment', time: '3 days ago', icon: CheckCircle }
  ];

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">{facility.name}</h1>
          <p className="text-gray-600">{facility.location} • Managed by {facility.manager}</p>
        </div>
        <div className="text-right">
          <div className="text-2xl font-bold text-green-600">{facility.esgScore}</div>
