                    <Icon className={`w-5 h-5 ${metric.color} mr-3`} />
                    <span className="text-sm font-medium text-gray-700">{metric.label}</span>
                  </div>
                  <span className="text-sm font-bold text-gray-900">
                    {metric.value.toLocaleString()} {metric.unit}
